import { useEffect, useState } from 'react';
import {
  Container, Typography, Grid, Card, CardContent, Box,
  CircularProgress, Paper, Chip, Divider, Button
} from '@mui/material';
import { useSystemStatus } from '../hooks/useSystemStatus';
import { useAgents } from '../hooks/useAgents';
import { useWorkflows } from '../hooks/useWorkflows';

const Dashboard = () => {
  const { systemStatus, loading: systemLoading, getSystemStatus } = useSystemStatus();
  const { agents, loading: agentsLoading, getAgents } = useAgents();
  const { workflows, loading: workflowsLoading, getWorkflows } = useWorkflows();
  const [isRefreshing, setIsRefreshing] = useState(false);
  
  useEffect(() => {
    getSystemStatus();
    getAgents();
    getWorkflows();
  }, [getSystemStatus, getAgents, getWorkflows]);
  
  const refreshData = async () => {
    setIsRefreshing(true);
    try {
      await Promise.all([
        getSystemStatus(),
        getAgents(),
        getWorkflows()
      ]);
    } finally {
      setIsRefreshing(false);
    }
  };

  const loading = systemLoading || agentsLoading || workflowsLoading || isRefreshing;
  
  const getStatusColor = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'online':
      case 'active':
      case 'running':
        return 'success';
      case 'offline':
      case 'inactive':
      case 'stopped':
        return 'error';
      default:
        return 'warning';
    }
  };
  
  const getActiveAgentsCount = () => {
    return agents?.filter(agent => agent.status === 'active').length || 0;
  };
  
  const getActiveWorkflowsCount = () => {
    return workflows?.filter(workflow => workflow.status === 'running').length || 0;
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Dashboard
        </Typography>
        <Button 
          variant="outlined" 
          onClick={refreshData} 
          disabled={loading}
        >
          {loading ? <CircularProgress size={24} /> : 'Refresh'}
        </Button>
      </Box>
      
      <Grid container spacing={3}>
        {/* System Status */}
        <Grid size={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" component="h2" gutterBottom>
              System Status
            </Typography>
            {systemLoading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
                <CircularProgress />
              </Box>
            ) : (
              <Grid container spacing={2}>
                <Grid size={{ xs: 12, md: 4 }}>
                  <Card>
                    <CardContent>
                      <Typography color="textSecondary" gutterBottom>
                        Status
                      </Typography>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <Chip 
                          label={systemStatus?.status || 'Unknown'} 
                          color={getStatusColor(systemStatus?.status || '') as any}
                          sx={{ mr: 1 }}
                        />
                        <Typography variant="h5" component="div">
                          {systemStatus?.status || 'Unknown'}
                        </Typography>
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid size={{ xs: 12, md: 4 }}>
                  <Card>
                    <CardContent>
                      <Typography color="textSecondary" gutterBottom>
                        Uptime
                      </Typography>
                      <Typography variant="h5" component="div">
                        {systemStatus?.uptime || 'N/A'}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid size={{ xs: 12, md: 4 }}>
                  <Card>
                    <CardContent>
                      <Typography color="textSecondary" gutterBottom>
                        Version
                      </Typography>
                      <Typography variant="h5" component="div">
                        {systemStatus?.version || 'Unknown'}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>
            )}
          </Paper>
        </Grid>
        
        {/* Agents Overview */}
        <Grid size={{ xs: 12, md: 6 }}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Typography variant="h6" component="h2" gutterBottom>
              Agents
            </Typography>
            {agentsLoading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
                <CircularProgress />
              </Box>
            ) : (
              <>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h4" component="div" sx={{ mr: 1 }}>
                    {getActiveAgentsCount()}
                  </Typography>
                  <Typography color="textSecondary">
                    / {agents?.length || 0} active
                  </Typography>
                </Box>
                <Divider sx={{ mb: 2 }} />
                {agents?.slice(0, 5).map((agent) => (
                  <Box key={agent.id} sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                    <Typography>{agent.name}</Typography>
                    <Chip 
                      label={agent.status} 
                      size="small" 
                      color={getStatusColor(agent.status) as any}
                    />
                  </Box>
                ))}
                {agents?.length > 5 && (
                  <Button href="/agents" size="small" sx={{ mt: 1 }}>
                    View all {agents.length} agents
                  </Button>
                )}
              </>
            )}
          </Paper>
        </Grid>
        
        {/* Workflows Overview */}
        <Grid size={{ xs: 12, md: 6 }}>
          <Paper sx={{ p: 2, height: '100%' }}>
            <Typography variant="h6" component="h2" gutterBottom>
              Workflows
            </Typography>
            {workflowsLoading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
                <CircularProgress />
              </Box>
            ) : (
              <>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h4" component="div" sx={{ mr: 1 }}>
                    {getActiveWorkflowsCount()}
                  </Typography>
                  <Typography color="textSecondary">
                    / {workflows?.length || 0} running
                  </Typography>
                </Box>
                <Divider sx={{ mb: 2 }} />
                {workflows?.slice(0, 5).map((workflow) => (
                  <Box key={workflow.id} sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                    <Typography>{workflow.name}</Typography>
                    <Chip 
                      label={workflow.status} 
                      size="small" 
                      color={getStatusColor(workflow.status) as any}
                    />
                  </Box>
                ))}
                {workflows?.length > 5 && (
                  <Button href="/workflows" size="small" sx={{ mt: 1 }}>
                    View all {workflows.length} workflows
                  </Button>
                )}
              </>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
