import { Navigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';

interface PublicRouteProps {
  children: React.ReactNode;
  redirectPath?: string;
}

const PublicRoute = ({ children, redirectPath = '/dashboard' }: PublicRouteProps) => {
  const { isAuthenticated, initialCheckDone } = useAuth();

  // During initial auth check, show nothing (or a loading spinner)
  if (!initialCheckDone) {
    return <div>Loading...</div>;
  }

  // If already authenticated, redirect to dashboard or specified path
  if (isAuthenticated) {
    return <Navigate to={redirectPath} replace />;
  }

  // If not authenticated, render the children
  return <>{children}</>;
};

export default PublicRoute;
