import { useEffect, useState } from 'react';
import {
  Container, Typography, Paper, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, Button, Chip, Box,
  CircularProgress, IconButton, Dialog, DialogTitle, DialogContent,
  DialogActions, TextField, FormControl, InputLabel, Select, MenuItem,
  Tabs, Tab
} from '@mui/material';
import { 
  Refresh as RefreshIcon, 
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  PlayArrow as StartIcon,
  Stop as StopIcon
} from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { 
  fetchWorkflowsAsync, 
  addWorkflowAsync, 
  updateWorkflowAsync, 
  deleteWorkflowAsync,
  startWorkflowAsync,
  stopWorkflowAsync
} from '../store/slices/workflows.slice';
import { Workflow, WorkflowRun } from '../types';

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

const TabPanel = (props: TabPanelProps) => {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`workflow-tabpanel-${index}`}
      aria-labelledby={`workflow-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ pt: 2 }}>
          {children}
        </Box>
      )}
    </div>
  );
};

const a11yProps = (index: number) => {
  return {
    id: `workflow-tab-${index}`,
    'aria-controls': `workflow-tabpanel-${index}`,
  };
};

const Workflows = () => {
  const dispatch = useAppDispatch();
  const { workflows, workflowRuns, status, error } = useAppSelector((state) => state.workflows);
  const [tabValue, setTabValue] = useState(0);
  const [openDialog, setOpenDialog] = useState(false);
  const [currentWorkflow, setCurrentWorkflow] = useState<Workflow | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    schedule: '',
    config: '',
    agentIds: [],
    status: 'inactive'
  });

  useEffect(() => {
    dispatch(fetchWorkflowsAsync());
  }, [dispatch]);

  const handleTabChange = (_event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  const handleRefresh = () => {
    dispatch(fetchWorkflowsAsync());
  };

  const handleOpenDialog = (workflow?: Workflow) => {
    if (workflow) {
      setCurrentWorkflow(workflow);
      setFormData({
        name: workflow.name,
        description: workflow.description || '',
        schedule: workflow.schedule || '',
        config: JSON.stringify(workflow.config, null, 2),
        agentIds: workflow.agentIds || [],
        status: workflow.status
      });
    } else {
      setCurrentWorkflow(null);
      setFormData({
        name: '',
        description: '',
        schedule: '',
        config: '{}',
        agentIds: [],
        status: 'inactive'
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleFormChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    if (name) {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const handleSubmit = async () => {
    try {
      let config;
      try {
        config = JSON.parse(formData.config);
      } catch (e) {
        alert('Invalid JSON in config field');
        return;
      }
      
      const workflowData = {
        ...formData,
        config
      };
      
      if (currentWorkflow) {
        await dispatch(updateWorkflowAsync({ id: currentWorkflow.id, ...workflowData }));
      } else {
        await dispatch(addWorkflowAsync(workflowData));
      }
      
      handleCloseDialog();
    } catch (error) {
      console.error('Error saving workflow:', error);
    }
  };

  const handleDeleteWorkflow = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this workflow?')) {
      try {
        await dispatch(deleteWorkflowAsync(id));
      } catch (error) {
        console.error('Error deleting workflow:', error);
      }
    }
  };

  const handleStartWorkflow = async (id: string) => {
    try {
      await dispatch(startWorkflowAsync(id));
    } catch (error) {
      console.error('Error starting workflow:', error);
    }
  };

  const handleStopWorkflow = async (id: string) => {
    try {
      await dispatch(stopWorkflowAsync(id));
    } catch (error) {
      console.error('Error stopping workflow:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'running':
        return 'success';
      case 'stopped':
        return 'error';
      case 'paused':
        return 'warning';
      default:
        return 'default';
    }
  };

  const loading = status === 'loading';

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          Workflows
        </Typography>
        <Box>
          <Button 
            variant="contained" 
            startIcon={<AddIcon />} 
            onClick={() => handleOpenDialog()}
            sx={{ mr: 1 }}
          >
            Add Workflow
          </Button>
          <Button 
            variant="outlined" 
            startIcon={<RefreshIcon />} 
            onClick={handleRefresh}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Refresh'}
          </Button>
        </Box>
      </Box>

      {error && (
        <Paper sx={{ p: 2, mb: 3, bgcolor: 'error.light', color: 'error.contrastText' }}>
          <Typography>{error}</Typography>
        </Paper>
      )}

      <Paper sx={{ width: '100%' }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs value={tabValue} onChange={handleTabChange} aria-label="workflow tabs">
            <Tab label="Workflows" {...a11yProps(0)} />
            <Tab label="Run History" {...a11yProps(1)} />
          </Tabs>
        </Box>
        
        <TabPanel value={tabValue} index={0}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Name</TableCell>
                  <TableCell>Description</TableCell>
                  <TableCell>Schedule</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Last Run</TableCell>
                  <TableCell align="right">Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {loading ? (
                  <TableRow>
                    <TableCell colSpan={6} align="center" sx={{ py: 3 }}>
                      <CircularProgress />
                    </TableCell>
                  </TableRow>
                ) : workflows.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} align="center">
                      No workflows found. Create your first workflow.
                    </TableCell>
                  </TableRow>
                ) : (
                  workflows.map((workflow) => (
                    <TableRow key={workflow.id}>
                      <TableCell>{workflow.name}</TableCell>
                      <TableCell>{workflow.description || '-'}</TableCell>
                      <TableCell>{workflow.schedule || 'Manual'}</TableCell>
                      <TableCell>
                        <Chip 
                          label={workflow.status} 
                          color={getStatusColor(workflow.status) as any}
                          size="small"
                        />
                      </TableCell>
                      <TableCell>{workflow.lastRun ? new Date(workflow.lastRun).toLocaleString() : 'Never'}</TableCell>
                      <TableCell align="right">
                        {workflow.status === 'running' ? (
                          <IconButton 
                            size="small"
                            onClick={() => handleStopWorkflow(workflow.id)}
                            aria-label="stop"
                            color="error"
                          >
                            <StopIcon />
                          </IconButton>
                        ) : (
                          <IconButton 
                            size="small"
                            onClick={() => handleStartWorkflow(workflow.id)}
                            aria-label="start"
                            color="success"
                          >
                            <StartIcon />
                          </IconButton>
                        )}
                        <IconButton 
                          size="small" 
                          onClick={() => handleOpenDialog(workflow)}
                          aria-label="edit"
                        >
                          <EditIcon />
                        </IconButton>
                        <IconButton 
                          size="small" 
                          onClick={() => handleDeleteWorkflow(workflow.id)}
                          aria-label="delete"
                          color="error"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
        
        <TabPanel value={tabValue} index={1}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Workflow</TableCell>
                  <TableCell>Start Time</TableCell>
                  <TableCell>End Time</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Duration</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {loading ? (
                  <TableRow>
                    <TableCell colSpan={5} align="center" sx={{ py: 3 }}>
                      <CircularProgress />
                    </TableCell>
                  </TableRow>
                ) : workflowRuns.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={5} align="center">
                      No workflow runs found.
                    </TableCell>
                  </TableRow>
                ) : (
                  workflowRuns.map((run: WorkflowRun) => {
                    const workflow = workflows.find(w => w.id === run.workflowId);
                    const duration = run.endTime ? 
                      new Date(run.endTime).getTime() - new Date(run.startTime).getTime() : 
                      new Date().getTime() - new Date(run.startTime).getTime();
                    
                    // Format duration nicely
                    const formatDuration = (ms: number) => {
                      const seconds = Math.floor(ms / 1000);
                      const minutes = Math.floor(seconds / 60);
                      const hours = Math.floor(minutes / 60);
                      
                      return hours > 0 ? 
                        `${hours}h ${minutes % 60}m ${seconds % 60}s` : 
                        minutes > 0 ? 
                          `${minutes}m ${seconds % 60}s` : 
                          `${seconds}s`;
                    };
                    
                    return (
                      <TableRow key={run.id}>
                        <TableCell>{workflow?.name || run.workflowId}</TableCell>
                        <TableCell>{new Date(run.startTime).toLocaleString()}</TableCell>
                        <TableCell>
                          {run.endTime ? new Date(run.endTime).toLocaleString() : 'Running...'}
                        </TableCell>
                        <TableCell>
                          <Chip 
                            label={run.status} 
                            color={
                              run.status === 'completed' ? 'success' :
                              run.status === 'failed' ? 'error' :
                              run.status === 'running' ? 'info' : 'default'
                            } 
                            size="small"
                          />
                        </TableCell>
                        <TableCell>{formatDuration(duration)}</TableCell>
                      </TableRow>
                    );
                  })
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>
      </Paper>

      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          {currentWorkflow ? 'Edit Workflow' : 'Add New Workflow'}
        </DialogTitle>
        <DialogContent>
          <TextField
            margin="dense"
            name="name"
            label="Name"
            type="text"
            fullWidth
            variant="outlined"
            value={formData.name}
            onChange={handleFormChange}
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            name="description"
            label="Description"
            type="text"
            fullWidth
            variant="outlined"
            value={formData.description}
            onChange={handleFormChange}
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            name="schedule"
            label="Schedule (cron expression)"
            type="text"
            fullWidth
            variant="outlined"
            value={formData.schedule}
            onChange={handleFormChange}
            sx={{ mb: 2 }}
            helperText="Leave empty for manual execution only"
          />
          <FormControl fullWidth margin="dense" sx={{ mb: 2 }}>
            <InputLabel id="workflow-status-label">Status</InputLabel>
            <Select
              labelId="workflow-status-label"
              name="status"
              value={formData.status}
              onChange={handleFormChange}
              label="Status"
            >
              <MenuItem value="running">Running</MenuItem>
              <MenuItem value="stopped">Stopped</MenuItem>
              <MenuItem value="paused">Paused</MenuItem>
            </Select>
          </FormControl>
          <TextField
            margin="dense"
            name="config"
            label="Configuration (JSON)"
            multiline
            rows={8}
            fullWidth
            variant="outlined"
            value={formData.config}
            onChange={handleFormChange}
            sx={{ mb: 2 }}
            helperText="Enter the workflow configuration in JSON format"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button onClick={handleSubmit} variant="contained">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Workflows;
